.zoom-image-container {
    position: relative;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    overflow: visible; /* Allow image to expand outside container */
}

.zoom-image {
    width: 100%;
    height: auto;
    border-radius: 8px;
    transition: transform 0.3s ease;
}

.zoom-image:hover {
    transform: scale(2);
    z-index: 100; /* Ensure zoomed image appears on top */
    position: relative;
}

