.image-row-i-scan {
  display: flex;
  gap: 24px;
  flex-wrap: nowrap; /* verhindert Umbruch */
  justify-content: flex-start;
  margin-bottom: 32px;
  overflow-x: auto; /* erm√∂glicht horizontales Scrollen */
}
.image-row-i-scan .image-container {
  position: relative;
  overflow: hidden;
  border-radius: 10px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.10);
  transition: box-shadow 0.2s;
  width: 320px;
  max-width: 100%;
  background: #fafbfc;
}
.image-row-i-scan .image-container:hover {
  box-shadow: 0 6px 24px rgba(0,0,0,0.18);
}
.image-row-i-scan img {
  display: block;
  width: 100%;
  height: auto;
  transition: transform 0.25s cubic-bezier(.4,2,.6,1);
}
.image-row-i-scan .image-container:hover img {
  transform: scale(1.07) rotate(-1deg);
  filter: brightness(1.08) contrast(1.08);
}
.image-row-i-scan .caption {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  background: rgba(30,34,40,0.82);
  color: #fff;
  font-size: 15px;
  padding: 10px 14px 8px 14px;
  opacity: 0.92;
  transition: background 0.2s;
  pointer-events: none;
  border-bottom-left-radius: 10px;
  border-bottom-right-radius: 10px;
  box-sizing: border-box;
}
