}
/* Hide TOC on smaller screens */
@media (max-width: 1400px) {
    .toc-sidebar {
        display: none;
    }
}

@media (max-width: 1200px) {
    div[style*="grid-template-columns: 1fr 1fr"] {
        display: block !important;
    }
    div[style*="grid-template-columns: 1fr 1fr"] > div:first-child {
        margin-bottom: 2rem;
    }
}

